mapper.insert:
INSERT INTO DApply (
	dcharId, dcname, dgroupId, dgroupName
) VALUES (
	#{dcharId}, #{dcname}, #{dgroupId}, #{dgroupName}
)

INSERT INTO DApply (
	dcharId, dcname, dgroupId, dgroupName, dapplyResult
) VALUES (
	#{dcharId}, #{dcname}, #{dgroupId}, #{dgroupName}, 'ACCEPTED'
)

mapper.select:
SELECT *
FROM DApply
WHERE dcharId = #{dcharId} AND dgroupId = #{dgroupId}

mapper.update:
UPDATE DApply
SET dapplyResult = #{dapplyResult}
WHERE dapplyId = #{dapplyId} AND dgroupId = #{dgroupId}
