mapper.insert:
INSERT INTO LApply (
	lcharId, lgroupId
) VALUES (
	#{lcharId}, #{lgroupId}
)

mapper.select:
SELECT lapplyId, lcharId, lgroupId, lapplyResult
FROM LApply
WHERE lapplyId = #{lapplyId} AND lgroupId = #{lgroupId}

SELECT lapplyId, lcharId, lgroupId, lapplyResult
FROM LApply
WHERE lgroupId = #{lgroupId}

mapper.update:
UPDATE LApply
SET lapplyResult = #{lapplyResult}
WHERE lapplyId = #{lapplyId} AND lgroupId = #{lgroupId}
