mapper.insert:
INSERT INTO LApply (
	lcharName, lgroupId
) VALUES (
	#{lcharName}, #{lgroupId}
)

mapper.select:
SELECT lapplyId, lcharName, lgroupId, lapplyResult
FROM LApply
WHERE lcharName = #{lcharName} AND lgroupId = #{lgroupId}

mapper.update:
UPDATE LApply
SET lapplyResult = #{lapplyResult}
WHERE lapplyId = #{lapplyId} AND lgroupId = #{lgroupId}
