mapper.insert:
INSERT INTO LApply (
	lcharName, lgroupId, lgroupName
) VALUES (
	#{lcharName}, #{lgroupId}, #{lgroupName}
)

INSERT INTO LApply (
	lcharName, lgroupId, lgroupName, lapplyResult
) VALUES (
	#{lcharName}, #{lgroupId}, #{lgroupName}, 'ACCEPTED'
)

mapper.select:
SELECT *
FROM LApply
WHERE lcharName = #{lcharName} AND lgroupId = #{lgroupId}

mapper.update:
UPDATE LApply
SET lapplyResult = #{lapplyResult}
WHERE lapplyId = #{lapplyId} AND lgroupId = #{lgroupId}
