mapper.insert:
INSERT INTO LGroup (
	lgroupOwner, lgroupType, lgroupMax, lgroupName, lgroupDate, lgroupDetail
) VALUES (
	#{lgroupOwner}, #{lgroupType}, #{lgroupMax}, #{lgroupName}, #{lgroupDate}, #{lgroupDetail}
)

mapper.select:
SELECT lgroupId
FROM LGroup
WHERE lgroupOwner = #{lgroupOwner}, lgroupType = #{lgroupType}, lgroupName = #{lgroupName}, lgroupDetail = #{lgroupDetail}, lgroupDate = #{lgroupDate}


SELECT lgroupId, lgroupOwner, lgroupType, lgroupMax, lgroupName, lgroupDate, lgroupDetail
FROM LGroup, LChar
WHERE LGroup.lgroupOnwer = LChar.lcharId AND LChar.uid = #{uid}

mapper.update:
UPDATE LGroup
SET lgroupType = #{lgroupType}, lgroupMax = #{lgroupMax}
WHERE lgropuID = #{lgropuID}
