mapper.insert:
INSERT INTO User (
	uid, uname, eamil, pw
) VALUES (
	#{uid}, #{uname}, #{email}, #{pw}
)

mapper.select:
SELECT uid, uname, eamil, pw
FROM User
WHERE uid = #{uid}

SELECT uid, uname, eamil, pw
FROM User
WHERE uname = #{uname}

SELECT uid, uname, eamil, pw
FROM User
WHERE email = #{email}

mapper.update:
UPDATE User
SET pw = #{pw}
WHERE uid = #{uid}

UPDATE User
SET uname = #{uname}
WHERE uid = #{uid}
